mapper.update:
update user set updateTime=CURRENT_TIMESTAMP

    ,username=#{username} 


    ,password=#{password} 


    ,operateName=#{operateName}


    ,tel=#{tel}


    ,email=#{email}


    ,employCard=#{employCard}

where userId=#{userId} 

   and updateTime=#{updateTime}

mapper.select:
		select u.userId,u.username,u.operateName,u.tel,u.email ,u.lastLoginTime,u.employCard
        from user u
        where status=${@com.ytoxl.module.user.dataobject.User@STATUS_ABLE} 
        
          and  u.userId in
            
             #{createByUserId}  
            
        
        
          and  (u.username like concat('%',#{username},'%') or u.operateName like concat('%',#{username},'%') or u.tel like concat('%',#{username},'%') )
        
		
			order by ${sort}	
			
				${dir}	
			
		
		limit #{start},#{limit}

		select count(u.userId) 
               from user u
        where status=${@com.ytoxl.module.user.dataobject.User@STATUS_ABLE} 
        
          and  u.userId in
            
             #{createByUserId}  
            
        
        
           and  (u.username like concat('%',#{username},'%') or u.operateName like concat('%',#{username},'%') or u.tel like concat('%',#{username},'%') )

mapper.update:
		update user set status=#{status} 
        where   userId in
          
             #{userId}

mapper.select:
		select u.userId,u.username,u.operateName,u.tel,u.email ,u.lastLoginTime,u.employCard
        from user u
        where status=${@com.ytoxl.module.user.dataobject.User@STATUS_ABLE} 
        
          and  u.userId in
            
             #{createByUserId}  
            
        
        
          and  u.username like concat('%',#{username},'%')
        
        
          and  u.operateName like concat('%',#{operateName},'%')
        
        
          and  u.tel like concat('%',#{tel},'%')
        
        
          and  u.email like concat('%',#{email},'%')
        
		
			order by ${sort}	
			
				${dir}	
			
		
		limit #{start},#{limit}
